$(document).ready(function(){
  $(".item1").hover(function(){
    $(".title1").css("font-weight","900");
    $(".item1").animate({
      top: "-8px"
    });
  $(".item1").mouseleave(function(){
    $(".title1").css("font-weight","300");
    $(".item1").animate({
      top: "0px"
    },300);
  });
  });
});


